import React, { useState } from 'react';
import axios from 'axios';
axios.defaults.headers.post['Content-Type'] = 'application/json';

function App() {
  const [a, setA] = useState(0);
  const [b, setB] = useState(0);
  const [operation, setOperation] = useState('sum');
  const [result, setResult] = useState(null);

  const calculate = async () => {
    try {

      //const response = await axios.post(`http://localhost:3001/${operation}`, { a: Number(a), b: Number(b) });
      //const response = await axios.post(`http://localhost:3001/subtract`);

      let response = () => {
        return new Promise(function(resolve, reject) {
          fetch('http://localhost:3001/subtract').then(response => {
            resolve(response);
          });
        });
      };
      let responseData = await response();
      console.log(responseData.data);
      setResult(responseData.result);
    } catch (error) {
      setResult(error.response.data.error || 'An error occurred');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Simple Calculator</h1>
      <input
        type="number"
        value={a}
        onChange={(e) => setA(e.target.value)}
        placeholder="Enter first number"
        style={{ marginRight: '10px' }}
      />
      <input
        type="number"
        value={b}
        onChange={(e) => setB(e.target.value)}
        placeholder="Enter second number"
        style={{ marginRight: '10px' }}
      />
      <select value={operation} onChange={(e) => setOperation(e.target.value)} style={{ marginRight: '10px' }}>
        <option value="sum">+</option>
        <option value="subtract">-</option>
        <option value="multiply">*</option>
        <option value="divide">/</option>
      </select>
      <button onClick={calculate}>Calculate</button>
      {result !== null && (
        <div style={{ marginTop: '20px' }}>
          <h2>Result: {result}</h2>
        </div>
      )}
    </div>
  );
}

export default App;
